insert into tracktable  (id,   tabela,   podatakid,   podataksifra,   trackakcija,   vreme,   korisnik,   gorgjed,   gaplikacija,   ipadresa)  select tracktable_id_seq.nextval,         'mlpakcijaefekti',         id,         null,               1,         sysdate,         'adm',         (select max(sifra) from orgjed where sifra = otac),         'adm',         'ognjenmMAU'               from mlpakcijaefekti where mlpakcija = 24                           select * from tracktable where tabela = 'mlpakcijauslovi'select * from mlpakcijaefektidelete from tracktable where ipadresa='ognjenmMAU'